import React, { useState, useEffect } from 'react';
import { FaChartLine, FaEye, FaTrash } from 'react-icons/fa';

/**
 * Komponent wyświetlający przesłane badania z możliwością analizy, podglądu i usuwania.
 * Wyniki analizy są podzielone na sekcje: "Analiza oraz interpretacja" i "Tabela wyników".
 */
function PrzeslaneBadania({ files, loadingAnalysis, handleAnalyze, handleShowAnalysis, handleDelete, formatDate, styles, setFiles }) {
  // Stan do debugowania
  const [debugInfo, setDebugInfo] = useState({});

  // Style dla krótszych przycisków
  const shorterButtonStyle = {
    maxWidth: '30%',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    whiteSpace: 'nowrap',
    padding: '4px 8px',
    fontSize: '0.85em'
  };

  // Funkcja do wyodrębniania tabeli i tekstu z analizy
  const extractTableAndText = (htmlContent) => {
    // Tworzymy element div, aby użyć DOM API do manipulacji HTML
    const tempDiv = document.createElement('div');
    tempDiv.innerHTML = htmlContent;

    // Znajdujemy wszystkie tabele
    const tables = tempDiv.querySelectorAll('table');

    // Jeśli nie ma tabel, zwracamy oryginalny HTML jako tekst analizy
    if (tables.length === 0) {
      return {
        analysisText: htmlContent,
        tableHtml: ''
      };
    }

    // Klonujemy div z całą zawartością
    const textDiv = tempDiv.cloneNode(true);

    // Usuwamy wszystkie tabele z klonu, aby pozostawić tylko tekst
    textDiv.querySelectorAll('table').forEach(table => {
      table.parentNode.removeChild(table);
    });

    // Zbieramy wszystkie tabele do jednego stringa
    let allTables = '';
    tables.forEach(table => {
      allTables += table.outerHTML;
    });

    return {
      analysisText: textDiv.innerHTML,
      tableHtml: allTables
    };
  };

  // Funkcja do renderowania analizy w podzielonym formacie
  const renderAnalysis = (analysis) => {
    if (!analysis) return null;

    try {
      // Wyodrębniamy tabelę i tekst z analizy
      const { analysisText, tableHtml } = extractTableAndText(analysis);

      // Aktualizujemy informacje debugowania
      setDebugInfo({
        hasAnalysis: true,
        hasTable: tableHtml.length > 0,
        analysisTextLength: analysisText.length,
        tableHtmlLength: tableHtml.length
      });

      return (
        <div style={styles.analysisContainer}>
          <div style={styles.analysisSection}>
            <h3 style={styles.sectionHeader}>Analiza oraz interpretacja</h3>
            <div style={styles.analysisContent} dangerouslySetInnerHTML={{ __html: analysisText }} />
          </div>

          {tableHtml && (
            <div style={styles.analysisSection}>
              <h3 style={styles.sectionHeader}>Tabela wyników</h3>
              <div style={styles.tableContainer} dangerouslySetInnerHTML={{ __html: tableHtml }} />
            </div>
          )}
        </div>
      );
    } catch (error) {
      // W przypadku błędu, wyświetlamy całą analizę w jednej sekcji
      setDebugInfo({
        error: error.message,
        fullAnalysis: true
      });

      return (
        <div style={styles.analysisContainer}>
          <div style={styles.analysisSection}>
            <h3 style={styles.sectionHeader}>Analiza oraz interpretacja</h3>
            <div style={styles.analysisContent} dangerouslySetInnerHTML={{ __html: analysis }} />
          </div>
        </div>
      );
    }
  };

  // Funkcja do wyświetlania modalu z analizą
  const renderAnalysisModal = (doc) => {
    if (!doc || !doc.showAnalysis) return null;

    return (
      <div style={styles.modalOverlay} onClick={() => handleShowAnalysis(null)}>
        <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
          <div style={styles.modalHeader}>
            <h3>Analiza badań: {doc.filename}</h3>
            <button 
              style={styles.closeButton}
              onClick={() => handleShowAnalysis(null)}
            >
              &times;
            </button>
          </div>

          {renderAnalysis(doc.analysis)}
        </div>
      </div>
    );
  };

  return (
    <div>
      <h2>Przesłane badania</h2>

      {/* Panel debugowania - usuń w wersji produkcyjnej */}
      {Object.keys(debugInfo).length > 0 && (
        <div style={{
          background: '#f5f5f5',
          border: '1px solid #ddd',
          padding: '10px',
          margin: '10px 0',
          borderRadius: '4px',
          fontSize: '12px',
          fontFamily: 'monospace'
        }}>
          <h4>Debug Info:</h4>
          <pre>{JSON.stringify(debugInfo, null, 2)}</pre>
        </div>
      )}

      {files.documents.length === 0 ? (
        <p>Brak plików</p>
      ) : (
        <div>
          {files.documents.map(doc => (
            <div key={doc.id} style={styles.fileItem}>
              <div style={styles.fileHeader}>
                <div style={styles.fileName}>{doc.filename}</div>
                <div style={styles.fileDate}>{formatDate(doc.upload_date)}</div>
              </div>
              <div style={styles.fileActions}>
                {doc.analysis ? (
                  <button
                    style={{...styles.button, ...styles.smallButton, ...shorterButtonStyle}}
                    onClick={() => handleShowAnalysis(doc)}
                  >
                    <FaEye /> Pokaż analizę
                  </button>
                ) : (
                  <button
                    style={{...styles.button, ...styles.smallButton, ...shorterButtonStyle}}
                    onClick={() => handleAnalyze(doc)}
                    disabled={loadingAnalysis === doc.id}
                  >
                    {loadingAnalysis === doc.id ? (
                      <>Analizuję...</>
                    ) : (
                      <><FaChartLine /> Analizuj</>
                    )}
                  </button>
                )}
                <button
                  style={{...styles.button, ...styles.smallButton, ...styles.secondaryButton, ...shorterButtonStyle}}
                  onClick={() => handleDelete(doc.id)}
                >
                  <FaTrash /> Usuń
                </button>
              </div>

              {/* Wyświetlamy modal z analizą dla aktualnego dokumentu */}
              {doc.showAnalysis && renderAnalysisModal(doc)}
            </div>
          ))}

          {files.totalPages > 1 && (
            <div style={styles.pagination}>
              <button
                style={{...styles.pageButton}}
                onClick={() => setFiles(prev => ({ ...prev, page: prev.page - 1 }))}
                disabled={files.page === 1}
              >
                Poprzednia
              </button>
              <span>{files.page} z {files.totalPages}</span>
              <button
                style={{...styles.pageButton}}
                onClick={() => setFiles(prev => ({ ...prev, page: prev.page + 1 }))}
                disabled={files.page === files.totalPages}
              >
                Następna
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default PrzeslaneBadania;
