import React, { useState } from 'react';
import { FaChartLine, FaEye, FaTrash, FaBug } from 'react-icons/fa';

/**
 * Funkcja wydzielająca tabelę i podsumowanie z pola analysis.
 * Obsługuje zarówno tabele HTML, jak i tabele w markdown (``````).
 */
function extractSections(analysis) {
  if (!analysis) return { table: null, summary: null };

  let tableHtml = null;
  let summaryText = analysis;

  // 1. Szukamy tabeli jako czysty HTML
  const tableHtmlMatch = analysis.match(/<table[\s\S]*?<\/table>/i);
  if (tableHtmlMatch) {
    tableHtml = tableHtmlMatch[0];
    summaryText = analysis.replace(tableHtml, '');
  } else {
    // 2. Szukamy tabeli jako blok markdown ``````
    const tableMdMatch = analysis.match(/``````/i);
    if (tableMdMatch) {
      tableHtml = tableMdMatch[1];
      summaryText = analysis.replace(tableMdMatch[0], '');
    }
  }

  // Usuwamy nagłówki typu "Oto analiza...", "Wyniki badań laboratoryjnych są następujące:"
  summaryText = summaryText.replace(/^(Oto analiza Twoich wyników laboratoryjnych[^\n]*|Wyniki badań laboratoryjnych są następujące:)\s*/i, '');

  // Zamieniamy **pogrubienia** markdown na <b>
  summaryText = summaryText.replace(/\*\*(.+?)\*\*/g, '<b>$1</b>');

  // Zamieniamy nowe linie na <br> dla czytelności
  summaryText = summaryText.replace(/\n/g, '<br>');

  // Usuwamy nadmiarowe <br> na początku i końcu
  summaryText = summaryText.replace(/^(<br>)+/, '').replace(/(<br>)+$/, '');

  // Jeśli tabela była w markdown, zadbaj by była poprawnym HTML
  if (tableHtml && !tableHtml.trim().startsWith('<table')) {
    tableHtml = `<table ${tableHtml.trim().slice(5)}`; // Dodaj <table jeśli brakuje
  }

  return {
    table: tableHtml,
    summary: summaryText.trim()
  };
}

function PrzeslaneBadania({
  files,
  loadingAnalysis,
  handleAnalyze,
  handleShowAnalysis,
  handleDelete,
  formatDate,
  styles,
  setFiles
}) {
  const [rawAnalysis, setRawAnalysis] = useState(null);

  const shorterButtonStyle = {
    maxWidth: '30%',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    whiteSpace: 'nowrap',
    padding: '4px 8px',
    fontSize: '0.85em'
  };

  // Renderowanie sekcji analizy i tabeli
  const renderAnalysis = (analysis) => {
    if (!analysis) return null;
    const { table, summary } = extractSections(analysis);

    return (
      <div style={styles.analysisContainer}>
        <section style={styles.analysisSection}>
          <h3 style={styles.sectionHeader}>Analiza oraz interpretacja</h3>
          <div
            style={styles.analysisContent}
            dangerouslySetInnerHTML={{
              __html: summary || '<i>Brak podsumowania</i>'
            }}
          />
        </section>
        {table && (
          <section style={styles.analysisSection}>
            <h3 style={styles.sectionHeader}>Tabela wyników</h3>
            <div
              style={styles.tableContainer}
              dangerouslySetInnerHTML={{ __html: table }}
            />
          </section>
        )}
      </div>
    );
  };

  // Modal do podglądu surowych danych analizy
  const renderRawModal = (doc) => {
    if (!rawAnalysis) return null;
    return (
      <div style={styles.modalOverlay}>
        <div style={styles.modalContent}>
          <div style={styles.modalHeader}>
            <h3>Surowe dane analizy: {doc.filename}</h3>
            <button
              style={styles.closeButton}
              onClick={() => setRawAnalysis(null)}
            >
              &times;
            </button>
          </div>
          <pre
            style={{
              background: '#f8f8f8',
              padding: '1em',
              borderRadius: '5px',
              maxHeight: '400px',
              overflow: 'auto',
              fontSize: '0.95em'
            }}
          >
            {typeof rawAnalysis.analysis === 'string'
              ? rawAnalysis.analysis
              : JSON.stringify(rawAnalysis.analysis, null, 2)}
          </pre>
        </div>
      </div>
    );
  };

  // Modal do podglądu analizy (podzielone sekcje)
  const renderAnalysisModal = (doc) => {
    if (!doc || !doc.showAnalysis) return null;
    return (
      <div style={styles.modalOverlay}>
        <div style={styles.modalContent}>
          <div style={styles.modalHeader}>
            <h3>Analiza badań: {doc.filename}</h3>
            <button
              style={styles.closeButton}
              onClick={() => handleShowAnalysis(null)}
            >
              &times;
            </button>
          </div>
          {renderAnalysis(doc.analysis)}
        </div>
      </div>
    );
  };

  return (
    <div>
      <h2>Przesłane badania</h2>
      {files.documents.length === 0 ? (
        <p>Brak plików</p>
      ) : (
        <div>
          {files.documents.map(doc => (
            <div key={doc.id} style={styles.fileItem}>
              <div style={styles.fileHeader}>
                <div style={styles.fileName}>{doc.filename}</div>
                <div style={styles.fileDate}>{formatDate(doc.upload_date)}</div>
              </div>
              <div style={styles.fileActions}>
                {doc.analysis ? (
                  <>
                    <button
                      style={{ ...styles.button, ...styles.smallButton, ...shorterButtonStyle }}
                      onClick={() => handleShowAnalysis(doc)}
                    >
                      <FaEye /> Pokaż analizę
                    </button>
                    <button
                      style={{
                        ...styles.button,
                        ...styles.smallButton,
                        ...styles.secondaryButton,
                        ...shorterButtonStyle,
                        background: '#e2e2e2',
                        color: '#333'
                      }}
                      onClick={() => setRawAnalysis(doc)}
                    >
                      <FaBug /> Pokaż surowe dane
                    </button>
                  </>
                ) : (
                  <button
                    style={{ ...styles.button, ...styles.smallButton, ...shorterButtonStyle }}
                    onClick={() => handleAnalyze(doc)}
                    disabled={loadingAnalysis === doc.id}
                  >
                    {loadingAnalysis === doc.id ? (
                      <>Analizuję...</>
                    ) : (
                      <><FaChartLine /> Analizuj</>
                    )}
                  </button>
                )}
                <button
                  style={{ ...styles.button, ...styles.smallButton, ...styles.secondaryButton, ...shorterButtonStyle }}
                  onClick={() => handleDelete(doc.id)}
                >
                  <FaTrash /> Usuń
                </button>
              </div>
              {/* Modal z analizą */}
              {doc.showAnalysis && renderAnalysisModal(doc)}
              {/* Modal z surowymi danymi */}
              {rawAnalysis && rawAnalysis.id === doc.id && renderRawModal(doc)}
            </div>
          ))}
          {files.totalPages > 1 && (
            <div style={styles.pagination}>
              <button
                style={{ ...styles.pageButton }}
                onClick={() => setFiles(prev => ({ ...prev, page: prev.page - 1 }))}
                disabled={files.page === 1}
              >
                Poprzednia
              </button>
              <span>{files.page} z {files.totalPages}</span>
              <button
                style={{ ...styles.pageButton }}
                onClick={() => setFiles(prev => ({ ...prev, page: prev.page + 1 }))}
                disabled={files.page === files.totalPages}
              >
                Następna
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default PrzeslaneBadania;
